import React from 'react'
import { useState } from 'react'
import { Fragment } from 'react'
import axios from 'axios'

const UserPage = (props) => {
    const handlePageClick = props.handlePageClick;
    // HTML structure of this component
    const [user, setUser] = useState(
        {
            username: '',
            email: ''
        }
    );

    const handleUser = (event) => {
        const fieldName = event.target.name;
        const fieldValue = event.target.value;
        const thisUser = { ...user };
        thisUser[fieldName] = fieldValue;
        setUser(thisUser);
    }
   
    // HTML structure of this component
    return (
        <div className="App">
            <header className="App-header">
                <h3>User Account</h3>
                <label htmlFor='username'>Username: </label>
                <br /><label htmlFor='username'>Change Password: </label>
                <button onClick={() => handlePageClick('pwResetPage')}>
                    Change Password
                </button>
                <br /><text>Account Type: </text>

                <br /><br /><text>Email: </text>
                <button onClick={() => handlePageClick('UserPage')}>
                    Update Email
                </button>
            </header>
        </div >
    );
}

export default UserPage;
